import {makeAutoObservable} from "mobx"
import axios from 'axios';

export default class Store {
    isAuth = false;
    isLoading = false;
  
    constructor() {
      makeAutoObservable(this);
    }
  
    static setAuth(bool: boolean) {
      // Добавьте код, который вам необходимо выполнить при установке авторизации
      console.log('Setting auth:', bool);
      this.isAuth = bool; // Обновляем статическое свойство
    }
  }
