import React from 'react';
import { IService } from '../../models/IService';
import ServicesService from '../../services/services-service';
import { INews } from '../../models/INews';
import NewsService from '../../services/news-service';

export interface IServiced {
    _id: string;
    head: string;
    desc: string;
    headDesc: string;
    descSingle: string;
    videoLink: string;
  }

const NewsAdmin = () => {
  const [head, setHead] = React.useState<string>("");
const [date, setDate] = React.useState<string>("");
const [category, setCategory] = React.useState<string>("");
const [imgLink, setImgLink] = React.useState<string>("");
const [desc, setDesc] = React.useState<string>("");
const [citata, setCitata] = React.useState<string>("");
const [headTwo, setHeadTwo] = React.useState<string>("");
const [descTwo, setDescTwo] = React.useState<string>("");
const [Tag, setTag] = React.useState<string>("");

const handleAddNew = async (e: any) => {
  e.preventDefault();
  
  // Создание объекта с данными новости
  const newRecord: INews = {
    _id: "", // Можно добавить логику для генерации ID, если необходимо
    head: head,
    date: date,
    category: category,
    imgLink: imgLink,
    desc: desc,
    citata: citata,
    headTwo: headTwo,
    descTwo: descTwo,
    Tag: Tag.split(",") // Преобразование строки с тегами в массив
  };

  // Вызовите функцию для создания новости
  try {
    const createdNewRecord = await NewsService.newsCreate(newRecord);
    console.log('Созданная новость:', createdNewRecord);
    // Дополнительная логика после успешного создания новости
  } catch (error) {
    console.error('Ошибка при создании новости:', error);
    // Обработка ошибок, если необходимо
  }
};

  
  
  

  return (
    <div className='adminService'>
      <h1 className="content__head quest-form__content-head content__head__headOne">
        AddService
      </h1>
      <form action="" method="post" style={{ alignItems: "flex-start", gap: "20px" }}>
        <input className="input" value={head} type="text" name="name" id="name" placeholder="Head" onChange={(e) => setHead(e.target.value)} />
        <textarea className="input" value={desc} name="desc" id="desc1" placeholder="Desc" onChange={(e) => setDesc(e.target.value)}></textarea>
        <textarea className="input" value={headDesc} name="desc" id="desc2" placeholder="HeadDesc" onChange={(e) => setHeadDesc(e.target.value)}></textarea>
        <input className="input" value={descSingle} type="text" name="name" id="name" placeholder="descSingle" onChange={(e) => setDescSingle(e.target.value)} />
        <button className="button about-form__button button__default" onClick={(e)=>handleAddService(e)}>
          Add Now
        </button>
      </form>
    </div>
  );
};

export default ServiceAdmin;
