import React, { ChangeEvent, useState } from 'react';
import { INews } from '../../models/INews';
import NewsService from '../../services/news-service';

const NewsAdmin = () => {
  const [head, setHead] = useState<string>("");
  const [date, setDate] = useState<string>("");
  const [category, setCategory] = useState<string>("");
  const [imgLink, setImgLink] = useState<string>("");
  const [desc, setDesc] = useState<string>("");
  const [citata, setCitata] = useState<string>("");
  const [headTwo, setHeadTwo] = useState<string>("");
  const [descTwo, setDescTwo] = useState<string>("");
  const [Tag, setTag] = useState<string>("");
  const [imageFile, setImageFile] = useState<File | null>(null);

  const handleAddNew = async (e: any) => {
    e.preventDefault();

    // Создание объекта с данными новости
    const newRecord: INews = {
      _id: "", // Можно добавить логику для генерации ID, если необходимо
      head: head,
      date: date,
      category: category,
      imgLink: imgLink,
      desc: desc,
      citata: citata,
      headTwo: headTwo,
      descTwo: descTwo,
      Tag: Tag.split(",") // Преобразование строки с тегами в массив
    };

    // Вызовите функцию для создания новости
    try {
      const createdNewRecord = await NewsService.newsCreate(newRecord);
      console.log('Созданная новость:', createdNewRecord);
      // Дополнительная логика после успешного создания новости
    } catch (error) {
      console.error('Ошибка при создании новости:', error);
      // Обработка ошибок, если необходимо
    }
  };

  const handleImageChange = (e: ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files && e.target.files[0];
    if (file) {
      setImageFile(file);
    }
  };

  return (
    <div className='adminService'>
      <h1 className="content__head quest-form__content-head content__head__headOne">
        Add News
      </h1>
      <form action="" method="post" style={{ alignItems: "flex-start", gap: "20px" }}>
        <input className="input" value={head} type="text" name="name" id="name" placeholder="Head" onChange={(e) => setHead(e.target.value)} />
        <textarea className="input" value={desc} name="desc" id="desc1" placeholder="Desc" onChange={(e) => setDesc(e.target.value)}></textarea>
        <textarea className="input" value={headTwo} name="desc" id="desc2" placeholder="HeadDesc" onChange={(e) => setHeadTwo(e.target.value)}></textarea>
        <input className="input" value={descTwo} type="text" name="name" id="name" placeholder="descSingle" onChange={(e) => setDescTwo(e.target.value)} />
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <button className="button about-form__button button__default" onClick={handleAddNew}>
          Add Now
        </button>
      </form>
    </div>
  );
};

export default NewsAdmin;
