import {AxiosResponse} from "axios";
import $api from "../http";



export default class MailService{
    static async JoinMail(){
        return $api.get(`/news`)
    }
    static async ContactMail(name:string, email:string, subject:string, message:string){
        return $api.post(`/contactusmail`, [name, email, subject, message])
    }
    
}
